package ImageConvertor.views.desktop;

import java.util.Base64;

import javax.swing.ImageIcon;
import javax.swing.JLabel;
import javax.swing.JPanel;

@SuppressWarnings("serial")
class StubImage extends JPanel {
	String notLoadedImage = "";

	public StubImage() {
		ImageIcon img = new ImageIcon(Base64.getDecoder().decode(notLoadedImage.getBytes()));
		JLabel l = new JLabel();
		l.setIcon(img);
		add(l);
	}
}
